import React, { useState, useEffect } from "react";
import API from "../api";

export default function LoginPage() {
  const [role, setRole] = useState("student"); // teacher or student
  const [mode, setMode] = useState("login"); // login | register | forgot | verify_otp
  const [form, setForm] = useState({ name: "", email: "", password: "", emailOrPhone: "", otp: "" });

  // Google Identity: render a button after the library loads
  useEffect(() => {
    const interval = setInterval(() => {
      if (window.google && document.getElementById("gsi-btn")) {
        clearInterval(interval);
        window.google.accounts.id.initialize({
          client_id: process.env.REACT_APP_GOOGLE_CLIENT_ID,
          callback: handleGoogleCallback
        });
        window.google.accounts.id.renderButton(
          document.getElementById("gsi-btn"),
          { theme: "outline", size: "large", width: "320" }
        );
      }
    }, 500);
    return () => clearInterval(interval);
  }, []);

  async function handleGoogleCallback(response) {
    const idToken = response.credential;
    try {
      const res = await API.post("/auth/google-login", { idToken, role });
      localStorage.setItem("token", res.data.token);
      alert("Logged in via Google as " + (res.data.user.role));
    } catch (err) {
      console.error(err);
      alert("Google login failed");
    }
  }

  async function submitRegister(e) {
    e.preventDefault();
    try {
      const res = await API.post("/auth/register", { name: form.name, email: form.email, password: form.password, role });
      localStorage.setItem("token", res.data.token);
      alert("Registered & logged in");
    } catch (err) {
      alert(err.response?.data?.message || "Error");
    }
  }

  async function submitLogin(e) {
    e.preventDefault();
    try {
      const res = await API.post("/auth/login", { email: form.email, password: form.password });
      localStorage.setItem("token", res.data.token);
      alert("Logged in");
    } catch (err) {
      alert(err.response?.data?.message || "Login failed");
    }
  }

  async function requestOtp(e) {
    e.preventDefault();
    try {
      await API.post("/auth/request-otp", { emailOrPhone: form.emailOrPhone });
      setMode("verify_otp");
      alert("OTP sent. Check email/SMS.");
    } catch (err) {
      alert(err.response?.data?.message || "Error requesting OTP");
    }
  }

  async function verifyOtp(e) {
    e.preventDefault();
    try {
      await API.post("/auth/verify-otp", { emailOrPhone: form.emailOrPhone, otp: form.otp, newPassword: form.password });
      setMode("login");
      alert("Password reset. Please login with new password.");
    } catch (err) {
      alert(err.response?.data?.message || "Invalid OTP or error");
    }
  }

  return (
    <div style={{ maxWidth: 420, border: "1px solid #ddd", padding: 20, borderRadius: 8 }}>
      <div style={{ marginBottom: 10 }}>
        <label>
          <input type="radio" checked={role==="student"} onChange={()=>setRole("student")} /> Student
        </label>
        {" "}
        <label>
          <input type="radio" checked={role==="teacher"} onChange={()=>setRole("teacher")} /> Teacher
        </label>
      </div>

      <div style={{ marginBottom: 12 }}>
        <button onClick={()=>setMode("login")} disabled={mode==="login"}>Login</button>{" "}
        <button onClick={()=>setMode("register")} disabled={mode==="register"}>Register</button>{" "}
        <button onClick={()=>setMode("forgot")} disabled={mode==="forgot"}>Forgot Password</button>
      </div>

      {mode === "login" && (
        <form onSubmit={submitLogin}>
          <div><input required placeholder="Email" value={form.email} onChange={e=>setForm({...form, email:e.target.value})} /></div>
          <div><input type="password" required placeholder="Password" value={form.password} onChange={e=>setForm({...form, password:e.target.value})} /></div>
          <div style={{ marginTop: 8 }}>
            <button type="submit">Login</button>
            <button type="button" onClick={()=>{ setMode("forgot"); }}> Forgot password?</button>
          </div>
          <div style={{ marginTop: 12 }}>
            <div id="gsi-btn"></div>
            <div style={{ marginTop: 6 }}>or sign in with Google (button above)</div>
          </div>
        </form>
      )}

      {mode === "register" && (
        <form onSubmit={submitRegister}>
          <div><input required placeholder="Full name" value={form.name} onChange={e=>setForm({...form, name:e.target.value})} /></div>
          <div><input required placeholder="Email" value={form.email} onChange={e=>setForm({...form, email:e.target.value})} /></div>
          <div><input type="password" required placeholder="Password" value={form.password} onChange={e=>setForm({...form, password:e.target.value})} /></div>
          <div style={{ marginTop: 8 }}><button type="submit">Register</button></div>
        </form>
      )}

      {mode === "forgot" && (
        <form onSubmit={requestOtp}>
          <div><input required placeholder="Enter registered email or phone" value={form.emailOrPhone} onChange={e=>setForm({...form, emailOrPhone:e.target.value})} /></div>
          <div style={{ marginTop: 8 }}><button type="submit">Send OTP</button></div>
        </form>
      )}

      {mode === "verify_otp" && (
        <form onSubmit={verifyOtp}>
          <div><input required placeholder="Enter OTP" value={form.otp} onChange={e=>setForm({...form, otp:e.target.value})} /></div>
          <div><input type="password" required placeholder="New password" value={form.password} onChange={e=>setForm({...form, password:e.target.value})} /></div>
          <div style={{ marginTop: 8 }}><button type="submit">Verify OTP & Set Password</button></div>
        </form>
      )}
    </div>
  );
}
